static void Main(string[] args) { int[] zahlen = new[] { 4,33,7,1,23,12 }; int[] vergleich = new int[zahlen.Length]; while(true) { vergleich = zahlen; for(int i = 0;i < zahlen.Length - 1;i++) { if(zahlen[i] > zahlen[i + 1]) { int temp = zahlen[i]; zahlen[i] = zahlen[i + 1]; zahlen[i + 1] = temp; } for(int j = 0;j < zahlen.Length;j++) { Console.Write(vergleich[j] + " "); } Console.WriteLine("\n"); } Console.WriteLine("\n"); if(is_same(zahlen,vergleich) == true) break; } Console.ReadKey(); }
public static bool is_same(int[] firstArray,int[] secondArray) { if(firstArray.Length != secondArray.Length) return false; for(int i = 0;i < firstArray.Length;i++) { if(firstArray[i] != secondArray[i]) return false; Console.WriteLine(firstArray[i]); Console.WriteLine(secondArray[i]); } return true; }

